import React,{useCallback,useEffect,useState} from 'react'
import {Text, View ,ActivityIndicator} from 'react-native'
import { useSelector , useDispatch } from 'react-redux';
import * as actions from './store/actions';

const Dashboard=()=> {

    const [isLoading,SetIsLoding] = useState(false);

    const dispatch = useDispatch();

    const getTracksAction= useCallback(async ()=>{
        const artist='Coldplay';
        let action= actions.getTracksByArtist(artist);
        try{
            await dispatch(action);
        
        }
        catch(err){
            console.log(err.message)
        }
    },[actions.getTracksByArtist,dispatch])

    useEffect(()=>{
        getTracksAction()
    },[getTracksAction])


    return (
        <View style={{justifyContent: 'center',alignItems: 'center',flex:1}}>
            {isLoading ?(<ActivityIndicator size="large" color='black'/>):(<Text>aaa</Text>)}
        </View>
    )
}
export default Dashboard;
