import React from 'react'
import { StyleSheet, Text, View, Image } from 'react-native'
import Colors from '../Utilities/AppColors';

const TrackItem = (props)=> {



    return (
        <View style={styles.container}>

            <View style={styles.image_container}>
                <Image style={styles.image} source={{uri:props.item.artworkUrl100}} />
            </View>
            <View style={styles.info_container}></View>
            <View style={styles.extra_container}></View>

            <Text>{props.item.kind}</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    image:{width:100, resizeMode:'contain'},
    image_container:{width:'25%'},
    info_container:{width:'60%'},
    extra_container:{width:'15%'},
    container:{
        width:'100%',
        backgroundColor: Colors.white,
        marginBottom:5,
        borderRadius:6,
        flexDirection:'row',
        paddingVertical:10,
    },
});

export default TrackItem
