import React from 'react'
import { StyleSheet, Text, View, Image } from 'react-native'
import Colors from '../Utilities/AppColors';

const TrackItem = (props)=> {


    const millisToMinutesAndSeconds = (millis) => {
        var minutes = Math.floor(millis / 60000);
        var seconds = ((millis % 60000) / 1000).toFixed(0);
        return minutes + ":" + (seconds < 10 ? '0' : '') + seconds;
      }
      

    return (
        <View style={styles.container}>
            <View style={styles.image_container}>
                <Image style={styles.image} source={{uri:props.item.artworkUrl100}} />
            </View>
            <View style={styles.info_container}>
                <Text style={styles.track_name}>{props.item.trackName}</Text>
                <Text style={styles.stext}>Collection: {props.item.collectionName}</Text>
                <Text style={styles.track_time}>{millisToMinutesAndSeconds(props.item.trackTimeMillis)}</Text>
                <Text>releaseDate</Text>
            </View>
            <View style={styles.extra_container}></View>
            <Text>{props.item.kind}</Text>
        </View>
    )
}

const styles = StyleSheet.create({
    track_time:{
        fontFamily:'Poppins-Medium',
        fontSize:11, color:Colors.black,
    },
    stext:{
        fontFamily:'Poppins-Light',
        fontSize:10, color:Colors.black,
    },
    track_name:{
        fontFamily:'Poppins-Medium',
        fontSize:14, color:Colors.blue,
        letterSpacing:-0.2, lineHeight:18
    },
    image:{width:110, height:80, borderTopLeftRadius: 8, borderBottomLeftRadius:8},
    image_container:{width:'25%'},
    info_container:{width:'60%', padding:10},
    extra_container:{width:'15%'},
    container:{
        width:'100%',
        backgroundColor: Colors.white,
        marginBottom:5,
        borderRadius:8,
        flexDirection:'row',
    },
});

export default TrackItem
