import React,{useCallback,useEffect,useState} from 'react'
import {Text, View ,ActivityIndicator,FlatList, TextInput, TouchableOpacity} from 'react-native'
import { useSelector , useDispatch } from 'react-redux';
import * as actions from '../store/actions';
import TrackItem from './TrackItem';
import Colors from '../Utilities/AppColors';
import Styles from '../Utilities/AppStyle';

const Dashboard=()=> {

    const [isLoading,SetIsLoding] = useState(false);

    const dispatch = useDispatch();

    const getTracksAction= useCallback(async ()=>{
        SetIsLoding(true);
        const artist='Coldplay';
        let action= actions.getTracksByArtist(artist);
        try{
            await dispatch(action);
            SetIsLoding(false);
        }
        catch(err){
            console.log(err.message);
            SetIsLoding(false);
        }
    },[actions.getTracksByArtist,dispatch])

    useEffect(()=>{
        getTracksAction()
    },[getTracksAction])

    const itunesData = useSelector(state =>(state.allTracks));
    const [searchStr, setSearchStr] = useState('');

    return (
        <View style={Styles.container}>

            <View style={Styles.search_container}>
                <View style={{width:'85%'}}>
                    <TextInput
                        value={searchStr}
                        onChangeText={text => setSearchStr(text)}
                        keyboardType='default'
                        autoCapitalize={true}
                        placeholder='Search...'
                        style={{
                            width:'100%', backgroundColor:Colors.white,
                            borderRadius:8, paddingHorizontal:20, paddingVertical:10,
                            fontFamily:'Poppins-Regular', fontSize:16
                        }}
                    />
                </View>
                <View style={{width:'15%', alignItems:'flex-end'}}>
                    <TouchableOpacity style={{
                        width:'90%', backgroundColor:Colors.yellow,
                        borderRadius:8, alignItems:'center', height:50
                    }}>
                        <Text>
                            GO
                        </Text>
                    </TouchableOpacity>
                </View>
            </View>

            {isLoading ?(<ActivityIndicator size="large" color='black'/>)
            :
            (
                <View style={{width:'100%', paddingHorizontal:20}}>
                    {itunesData?.allTracks?.results?.length > 0 ?
                     (
                         <FlatList data={itunesData.allTracks.results}
                         keyExtractor={item=>item.trackId}
                         renderItem={track=><TrackItem item={track.item}/>}
                         />
                     )
                     :
                     (<Text>No</Text>)
                    }
                </View>
            )
            }
        </View>
    )
}
export default Dashboard;
