import React,{useCallback,useEffect,useState} from 'react'
import {Text, View ,ActivityIndicator,FlatList} from 'react-native'
import { useSelector , useDispatch } from 'react-redux';
import * as actions from './store/actions';

const Dashboard=()=> {

    const [isLoading,SetIsLoding] = useState(false);

    const dispatch = useDispatch();

    const getTracksAction= useCallback(async ()=>{
        SetIsLoding(true);
        const artist='Coldplay';
        let action= actions.getTracksByArtist(artist);
        try{
            await dispatch(action);
            SetIsLoding(false);
        
        }
        catch(err){
            console.log(err.message)
        }
    },[actions.getTracksByArtist,dispatch])

    useEffect(()=>{
        getTracksAction()
    },[getTracksAction])

    const itunesData = useSelector(state =>state.allTracks);
    //console.log(itunesData);

    return (
        <View style={{justifyContent: 'center',alignItems: 'center',flex:1}}>
            {isLoading ?(<ActivityIndicator size="large" color='black'/>)
            :
            (
                <View>
                    {itunesData.allTracks?.results?.length > 0 ?
                     (<Text>Yes</Text>)
                     :
                     (<Text>No</Text>)
                    }
                </View>
            )
            }
        </View>
    )
}
export default Dashboard;
